// Form handling (for future contact forms)
function initFormHandling() {
    const forms = document.querySelectorAll('form');

    forms.forEach(form => {
        form.addEventListener('submit', function(e) {
            e.preventDefault();

            // Add form validation and submission logic here
            const formData = new FormData(this);

            // Show loading state
            const submitBtn = this.querySelector('button[type="submit"]');
            const originalText = submitBtn.textContent;
            submitBtn.textContent = 'Envoi en cours...';
            submitBtn.disabled = true;

            // Simulate form submission
            setTimeout(() => {
                submitBtn.textContent = 'Message envoyé !';
                setTimeout(() => {
                    submitBtn.textContent = originalText;
                    submitBtn.disabled = false;
                    this.reset();
                }, 2000);
            }, 1000);
        });
    });
}

// Multi-Step Form handling
function initMultiStepForm() {
    const form = document.getElementById('multiStepForm');
    const nextBtn = document.getElementById('nextBtn');
    const prevBtn = document.getElementById('prevBtn');
    const submitBtn = document.getElementById('submitBtn');
    const progressSteps = document.querySelectorAll('.progress-step');
    const formSteps = document.querySelectorAll('.form-step');

    let currentStep = 1;
    const totalSteps = 4;

    if (!form) return;

    // Initialize file upload handlers
    initFileUploads();

    // Next button handler
    nextBtn.addEventListener('click', function() {
        if (validateCurrentStep()) {
            if (currentStep < totalSteps) {
                currentStep++;
                updateFormDisplay();
            }
        }
    });

    // Previous button handler
    prevBtn.addEventListener('click', function() {
        if (currentStep > 1) {
            currentStep--;
            updateFormDisplay();
        }
    });

    // Submit button handler
    submitBtn.addEventListener('click', function(e) {
        e.preventDefault();
        if (validateCurrentStep()) {
            submitForm();
        }
    });

    // Form submit handler
    form.addEventListener('submit', function(e) {
        e.preventDefault();
        if (validateCurrentStep()) {
            submitForm();
        }
    });

    function updateFormDisplay() {
        // Update progress steps
        progressSteps.forEach((step, index) => {
            if (index + 1 <= currentStep) {
                step.classList.add('active');
            } else {
                step.classList.remove('active');
            }
        });

        // Update form steps
        formSteps.forEach((step, index) => {
            if (index + 1 === currentStep) {
                step.classList.add('active');
            } else {
                step.classList.remove('active');
            }
        });

        // Update navigation buttons
        if (currentStep === 1) {
            prevBtn.style.display = 'none';
            nextBtn.style.display = 'inline-block';
            submitBtn.style.display = 'none';
        } else if (currentStep === totalSteps) {
            prevBtn.style.display = 'inline-block';
            nextBtn.style.display = 'none';
            submitBtn.style.display = 'inline-block';
        } else {
            prevBtn.style.display = 'inline-block';
            nextBtn.style.display = 'inline-block';
            submitBtn.style.display = 'none';
        }
    }

    function validateCurrentStep() {
        const currentStepElement = document.querySelector(`.form-step[data-step="${currentStep}"]`);
        const requiredFields = currentStepElement.querySelectorAll('[required]');
        let isValid = true;

        requiredFields.forEach(field => {
            if (!field.value.trim()) {
                isValid = false;
                field.style.borderColor = '#dc3545';
                field.style.boxShadow = '0 0 0 3px rgba(220, 53, 69, 0.1)';

                // Show error message
                showFieldError(field, 'Ce champ est obligatoire');
            } else {
                field.style.borderColor = '#e9ecef';
                field.style.boxShadow = 'none';
                clearFieldError(field);
            }
        });

        // Additional validation for specific steps
        if (currentStep === 1) {
            // Age validation
            const birthDate = document.getElementById('birthDate');
            if (birthDate.value) {
                const age = calculateAge(birthDate.value);
                if (age < 16 || age > 25) {
                    isValid = false;
                    showFieldError(birthDate, 'L\'âge doit être entre 16 et 25 ans');
                }
            }
        }

        if (!isValid) {
            alert('Veuillez corriger les erreurs avant de continuer.');
        }

        return isValid;
    }

    function calculateAge(birthDate) {
        const today = new Date();
        const birth = new Date(birthDate);
        let age = today.getFullYear() - birth.getFullYear();
        const monthDiff = today.getMonth() - birth.getMonth();

        if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
            age--;
        }

        return age;
    }

    function showFieldError(field, message) {
        clearFieldError(field);

        const errorDiv = document.createElement('div');
        errorDiv.className = 'field-error';
        errorDiv.textContent = message;
        errorDiv.style.color = '#dc3545';
        errorDiv.style.fontSize = '0.8rem';
        errorDiv.style.marginTop = '0.25rem';

        field.parentNode.appendChild(errorDiv);
    }

    function clearFieldError(field) {
        const existingError = field.parentNode.querySelector('.field-error');
        if (existingError) {
            existingError.remove();
        }
    }

    function submitForm() {
        const submitBtn = document.getElementById('submitBtn');
        const originalText = submitBtn.textContent;
        submitBtn.textContent = 'Envoi en cours...';
        submitBtn.disabled = true;

        // Add hidden fields for better email formatting
        const formData = new FormData(form);

        // Add a subject field for Formspree
        const subjectField = document.createElement('input');
        subjectField.type = 'hidden';
        subjectField.name = '_subject';
        subjectField.value = `Nouvelle candidature - ${formData.get('firstName')} ${formData.get('lastName')}`;
        form.appendChild(subjectField);

        // Add recipient email
        const recipientField = document.createElement('input');
        recipientField.type = 'hidden';
        recipientField.name = '_replyto';
        recipientField.value = formData.get('email');
        form.appendChild(recipientField);

        // Submit the form to Formspree
        form.submit();

        // Show success message (Formspree will redirect to a success page)
        submitBtn.textContent = 'Candidature envoyée !';
        submitBtn.style.background = '#28a745';

        const successMessage = document.createElement('div');
        successMessage.className = 'success-message';
        successMessage.innerHTML = `
            <div class="success-content">
                <h3>🎉 Candidature envoyée avec succès !</h3>
                <p>Nous avons bien reçu votre candidature. Notre équipe vous contactera dans les plus brefs délais.</p>
            </div>
        `;

        form.parentNode.insertBefore(successMessage, form);

        setTimeout(() => {
            submitBtn.textContent = originalText;
            submitBtn.disabled = false;
            submitBtn.style.background = '';
            form.reset();
            successMessage.remove();
            // Reset to first step
            currentStep = 1;
            updateFormDisplay();
        }, 3000);
    }
}

