        padding: 1rem;
    }

    .hero-cta {
        flex-direction: column;
        align-items: center;
    }

    .btn {
        padding: 0.8rem 1.5rem;
    }

    .container {
        padding: 0 1rem;
    }

    section {
        padding: 3rem 0;
    }
}

@media (max-width: 480px) {
    .nav-logo h2 {
        font-size: 1rem;
    }

    .models-grid {
        grid-template-columns: 1fr;
    }

    .footer-content {
        grid-template-columns: 1fr;
        text-align: center;
    }
}

/* Smooth scrolling */
html {
    scroll-behavior: smooth;
}

/* Loading animation for images */
img {
    transition: opacity 0.3s ease;
}

img:not([src]),
img[src=""] {
    opacity: 0;
}

/* Error message styling */
.error-message {
    text-align: center;
    padding: 2rem;
    color: var(--text-secondary);
    font-size: 1.1rem;
    background: var(--secondary-color);
    border-radius: 10px;
    margin: 2rem 0;
}
