style.css.content:
/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --primary-color: #000000;
    --secondary-color: #f8f8f8;
    --accent-color: #1a1a1a;
    --accent-light: #333333;
    --text-primary: #000000;
    --text-secondary: #666666;
    --text-light: #999999;
    --white: #ffffff;
    --transition: all 0.3s ease;
    --shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
    --shadow-hover: 0 20px 40px rgba(0, 0, 0, 0.15);
}

body {
    font-family: 'Poppins', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
    font-weight: 300;
    line-height: 1.6;
    color: var(--text-primary);
    background-color: var(--white);
    overflow-x: hidden;
}

/* Typography */
h1, h2, h3, h4, h5, h6 {
    font-weight: 300;
    line-height: 1.2;
    margin-bottom: 1rem;
}

h1 {
    font-size: clamp(2.5rem, 5vw, 4rem);
    font-weight: 300;
}

h2 {
    font-size: clamp(2rem, 4vw, 3rem);
    font-weight: 300;
}

h3 {
    font-size: clamp(1.5rem, 3vw, 2rem);
    font-weight: 300;
}

p {
    margin-bottom: 1rem;
    color: var(--text-secondary);
    font-weight: 200;
}

/* Container and Layout */
.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
}

/* Navigation - Resizable Navbar Effect */
.navbar {
    position: fixed;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 100%;
    max-width: 1400px;
    background: transparent;
    backdrop-filter: none;
    border-bottom: none;
    z-index: 1000;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    padding: 1rem 2rem;
}

.navbar.scrolled {
    top: 1rem;
    width: 90%;
    max-width: 1200px;
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(0, 0, 0, 0.1);
    border-radius: 50px;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.12);
    padding: 0.75rem 2rem;
}

@media (max-width: 768px) {
    .navbar.scrolled {
        width: 95%;
        border-radius: 30px;
        padding: 0.75rem 1rem;
    }
}

.nav-container {
    width: 100%;
    margin: 0;
    padding: 0;
    display: flex;
    justify-content: space-between;
    align-items: center;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

.nav-logo {
    display: flex;
    align-items: center;
    gap: 0.8rem;
}

.logo-icon {
    width: 40px;
    height: 40px;
    transition: all 0.3s ease;
}

.navbar.scrolled .logo-icon {
    width: 35px;
    height: 35px;
}

.nav-logo h2 {
    font-size: 1.2rem;
    font-weight: 700;
    color: var(--primary-color);
    margin: 0;
    transition: all 0.3s ease;
    white-space: nowrap;
    overflow: hidden;
}

.navbar.scrolled .nav-logo h2 {
    font-size: 1rem;
}

@media (max-width: 768px) {
    .nav-logo h2 {
        font-size: 1rem;
    }
    .navbar.scrolled .nav-logo h2 {
        font-size: 0.9rem;
    }
}

.nav-menu {
    display: flex;
    gap: 2rem;
    align-items: center;
    transition: all 0.3s ease;
}

.navbar.scrolled .nav-menu {
    gap: 1.5rem;
}

@media (min-width: 1200px) {
    .nav-menu {
        gap: 2.5rem;
    }
    .navbar.scrolled .nav-menu {
        gap: 2rem;
    }
    .nav-link {
        font-size: 1rem;
    }
}

@media (max-width: 768px) {
    .nav-menu {
        gap: 1rem;
    }
    .navbar.scrolled .nav-menu {
        gap: 0.75rem;
    }
}

.nav-link {
    text-decoration: none;
    color: var(--text-primary);
    font-weight: 500;
    transition: all 0.3s ease;
    position: relative;
    font-size: 0.95rem;
    white-space: nowrap;
}

.navbar.scrolled .nav-link {
    font-size: 0.9rem;
}

.nav-link:hover,
.nav-link.active {
    color: var(--accent-color);
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--accent-color);
    transition: var(--transition);
}

.nav-link:hover::after,
.nav-link.active::after {
    width: 100%;
}

/* Menu déroulant Details */
.nav-dropdown {
    position: relative;
    display: inline-block;
}

.nav-dropdown-toggle {
    text-decoration: none;
    color: var(--text-primary);
    font-weight: 500;
    transition: all 0.3s ease;
    position: relative;
    font-size: 0.95rem;
    white-space: nowrap;
    display: flex;
    align-items: center;
    gap: 5px;
    cursor: pointer;
}

.navbar.scrolled .nav-dropdown-toggle {
    font-size: 0.9rem;
}

.nav-dropdown-toggle i {
    font-size: 1.2em;
    transition: transform 0.3s ease;
}

.nav-dropdown:hover .nav-dropdown-toggle i {
    transform: rotate(180deg);
}

.nav-dropdown-content {
    display: none;
    position: absolute;
    top: 100%;
    left: 0;
    background: white;
    min-width: 200px;
    box-shadow: 0 8px 25px rgba(0,0,0,0.15);
    border-radius: 12px;
    padding: 10px 0;
    margin-top: 10px;
    z-index: 1000;
}

.nav-dropdown:hover .nav-dropdown-content {
    display: block;
    animation: fadeIn 0.3s ease;
}

.nav-dropdown-content a {
    display: block;
    padding: 12px 20px;
    text-decoration: none;
    color: var(--text-primary);
    font-weight: 400;
    font-size: 0.95rem;
    transition: all 0.3s ease;
}

.nav-dropdown-content a:hover {
    background: rgba(0,0,0,0.05);
    color: var(--accent-color);
    padding-left: 25px;
}

@keyframes fadeIn {
    from {
        opacity: 0;
        transform: translateY(-10px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.nav-cta {
    background: var(--accent-color);
    color: var(--white) !important;
    padding: 0.5rem 1rem;
    border-radius: 5px;
    transition: var(--transition);
}

.nav-cta:hover {
    background: var(--accent-light);
    transform: translateY(-2px);
}

.nav-cta::after {
    display: none;
}

.btn-inscription {
    background: var(--accent-color) !important;
    color: var(--white) !important;
    padding: 0.8rem 1.5rem !important;
    border-radius: 25px !important;
    font-weight: 600 !important;
    transition: all 0.3s ease !important;
    box-shadow: 0 4px 15px rgba(255, 107, 107, 0.3) !important;
}

.btn-inscription:hover {
    background: var(--accent-light) !important;
    transform: translateY(-2px) !important;
    box-shadow: 0 6px 20px rgba(255, 107, 107, 0.4) !important;
}

.btn-inscription::after {
    display: none !important;
}

/* Inscription Section */
.inscription {
    padding: 6rem 0;
    background: linear-gradient(135deg, var(--secondary-color) 0%, #f8f9fa 100%);
}

/* Multi-Step Form Styles */
.multi-step-form {
    max-width: 800px;
    margin: 0 auto;
    background: var(--white);
    border-radius: 15px;
    box-shadow: var(--shadow);
    overflow: hidden;
}

.form-progress {
    display: flex;
    background: var(--accent-color);
    padding: 1.5rem;
    gap: 1rem;
}

.progress-step {
    display: flex;
    flex-direction: column;
    align-items: center;
    flex: 1;
    color: rgba(255, 255, 255, 0.7);
    transition: var(--transition);
}

.progress-step.active {
    color: var(--white);
}

.step-number {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.2);
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    margin-bottom: 0.5rem;
    transition: var(--transition);
}

.progress-step.active .step-number {
    background: var(--white);
    color: var(--accent-color);
}

.step-title {
    font-size: 0.9rem;
    text-align: center;
    font-weight: 500;
}

.multi-step-form-container {
    padding: 2rem;
}

.form-step {
    display: none;
    animation: fadeIn 0.5s ease-in-out;
}

.form-step.active {
    display: block;
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

.form-step h3 {
    font-size: 1.5rem;
    font-weight: 700;
    color: var(--accent-color);
    margin-bottom: 1rem;
    text-transform: uppercase;
}

.age-requirement {
    color: #dc3545;
    font-weight: 500;
    margin-bottom: 2rem;
    font-size: 0.9rem;
}

.measurement-info {
    color: var(--text-secondary);
    margin-bottom: 0.5rem;
    font-size: 0.9rem;
}

.height-requirement {
    color: #dc3545;
    font-weight: 500;
    margin-bottom: 2rem;
    font-size: 0.9rem;
}

.form-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
    margin-bottom: 2rem;
}

.form-group {
    display: flex;
    flex-direction: column;
}

.form-group label {
    font-weight: 500;
    margin-bottom: 0.5rem;
    color: var(--text-primary);
    font-size: 0.9rem;
}

.form-group input,
.form-group select {
    padding: 0.75rem;
    border: 1px solid #e9ecef;
    border-radius: 8px;
    font-size: 1rem;
    transition: var(--transition);
    background: var(--white);
}

.form-group input:focus,
.form-group select:focus {
    outline: none;
    border-color: var(--accent-color);
    box-shadow: 0 0 0 3px rgba(0, 0, 0, 0.1);
}

.photo-instructions {
    color: var(--text-secondary);
    margin-bottom: 1.5rem;
    line-height: 1.6;
}

.photo-requirements {
    background: #f8f9fa;
    padding: 1.5rem;
    border-radius: 8px;
    margin-bottom: 2rem;
}

.photo-requirements li {
    margin-bottom: 0.5rem;
    color: var(--text-secondary);
}

.photo-upload-section {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
    margin-bottom: 2rem;
}

.photo-upload h4 {
    font-weight: 600;
    margin-bottom: 1rem;
    color: var(--accent-color);
    text-transform: uppercase;
}

.upload-area {
    border: 2px dashed #e9ecef;
    border-radius: 8px;
    padding: 2rem;
    text-align: center;
    transition: var(--transition);
    cursor: pointer;
}

.upload-area:hover {
    border-color: var(--accent-color);
    background: rgba(0, 0, 0, 0.02);
}

.upload-area input[type="file"] {
    display: none;
}

.upload-area label {
    cursor: pointer;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
}

.upload-text {
    font-weight: 500;
    color: var(--accent-color);
}

.file-name {
    font-size: 0.9rem;
    color: var(--text-light);
}

.video-upload-section {
    margin-bottom: 2rem;
}

.video-upload-section h4 {
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: var(--accent-color);
    text-transform: uppercase;
}

.video-constraint {
    color: #dc3545;
    font-size: 0.9rem;
    margin-bottom: 1rem;
}

.video-instructions {
    color: var(--text-secondary);
    margin-bottom: 2rem;
    line-height: 1.6;
}

.final-step {
    background: #f8f9fa;
    padding: 2rem;
    border-radius: 8px;
    margin-bottom: 2rem;
}

.final-step h4 {
    font-weight: 600;
    margin-bottom: 1rem;
    color: var(--accent-color);
    text-transform: uppercase;
}

.checkbox-group {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.checkbox-label {
    display: flex;
    align-items: flex-start;
    gap: 0.75rem;
    cursor: pointer;
    font-size: 0.9rem;
    line-height: 1.5;
}

.checkbox-label input[type="checkbox"] {
    margin-top: 0.2rem;
}

.link {
    color: var(--accent-color);
    text-decoration: underline;
}

.form-navigation {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding-top: 2rem;
    border-top: 1px solid #e9ecef;
}

.form-navigation .btn {
    min-width: 120px;
}

/* Responsive Design */
@media (max-width: 768px) {
    .form-progress {
        flex-direction: column;
        gap: 1rem;
    }

    .progress-step {
        flex-direction: row;
        text-align: left;
    }

    .step-number {
        margin-bottom: 0;
        margin-right: 1rem;
    }

    .form-grid {
        grid-template-columns: 1fr;
    }

    .photo-upload-section {
        grid-template-columns: 1fr;
    }

    .form-navigation {
        flex-direction: column;
        gap: 1rem;
    }

    .form-navigation .btn {
        width: 100%;
    }
}

.inscription-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: start;
}

.inscription-info h3 {
    color: var(--primary-color);
    margin-bottom: 2rem;
    font-size: 1.5rem;
}

.inscription-info ul {
    list-style: none;
    padding: 0;
}

.inscription-info li {
    padding: 1rem 0;
    border-bottom: 1px solid rgba(0, 0, 0, 0.1);
    font-size: 1.1rem;
    color: var(--text-color);
}

.inscription-info li:last-child {
    border-bottom: none;
}

.inscription-image {
    margin-top: 2rem;
    text-align: center;
    padding: 1rem;
}

.inscription-image img {
    width: 100%;
    max-width: 400px;
    height: auto;
    border-radius: 15px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
    background: linear-gradient(135deg, rgba(255, 107, 107, 0.05) 0%, rgba(78, 205, 196, 0.05) 100%);
}

.inscription-image img:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 40px rgba(0, 0, 0, 0.15);
}

/* Form Styling */
.form-styled {
    background: var(--white);
    padding: 2.5rem;
    border-radius: 20px;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
    border: 1px solid rgba(0, 0, 0, 0.05);
}

.form-group {
    margin-bottom: 1.5rem;
}

.form-group label {
    display: block;
    margin-bottom: 0.5rem;
    font-weight: 600;
    color: var(--primary-color);
    font-size: 0.95rem;
}

.form-group input,
.form-group select,
.form-group textarea {
    width: 100%;
    padding: 1rem;
    border: 2px solid #e9ecef;
    border-radius: 10px;
    font-size: 1rem;
    transition: all 0.3s ease;
    background: #f8f9fa;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: var(--accent-color);
    background: var(--white);
    box-shadow: 0 0 0 3px rgba(255, 107, 107, 0.1);
}

.form-group input::placeholder,
.form-group textarea::placeholder {
    color: #6c757d;
}

.form-row {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 1rem;
}

.checkbox-group {
    margin-top: 2rem;
}

.checkbox-label {
    display: flex;
    align-items: flex-start;
    gap: 0.75rem;
    cursor: pointer;
    font-size: 0.9rem;
    color: var(--text-color);
    line-height: 1.4;
}

.checkbox-label input[type="checkbox"] {
    width: auto;
    margin: 0;
    transform: scale(1.2);
}

.checkmark {
    position: relative;
    display: inline-block;
    width: 20px;
    height: 20px;
    background: #e9ecef;
    border-radius: 4px;
    flex-shrink: 0;
}

.checkbox-label input[type="checkbox"]:checked + .checkmark::after {
    content: '✓';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: var(--white);
    font-weight: bold;
    font-size: 12px;
}

.checkbox-label input[type="checkbox"]:checked + .checkmark {
    background: var(--accent-color);
}

.form-styled .btn {
    width: 100%;
    margin-top: 1rem;
    padding: 1.2rem;
    font-size: 1.1rem;
    font-weight: 600;
}

/* Responsive Design for Inscription */
@media (max-width: 768px) {
    .inscription-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .form-row {
        grid-template-columns: 1fr;
    }

    .form-styled {
        padding: 1.5rem;
    }

    .inscription-info h3 {
        font-size: 1.3rem;
    }

    .inscription-info li {
        font-size: 1rem;
    }
}

/* Success Message */
.success-message {
    position: fixed;
    top: 20px;
    right: 20px;
    background: #28a745;
    color: white;
    padding: 1.5rem;
    border-radius: 10px;
    box-shadow: 0 10px 30px rgba(40, 167, 69, 0.3);
    z-index: 1000;
    animation: slideInRight 0.5s ease-out;
    max-width: 400px;
}

.success-content h3 {
    margin: 0 0 0.5rem 0;
    font-size: 1.2rem;
}

.success-content p {
    margin: 0;
    opacity: 0.9;
}

@keyframes slideInRight {
    from {
        transform: translateX(100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

.hamburger {
    display: none;
    flex-direction: column;
    cursor: pointer;
    gap: 4px;
    padding: 8px;
    z-index: 1001;
}

.hamburger span {
    width: 25px;
    height: 3px;
    background: var(--primary-color);
    transition: var(--transition);
    border-radius: 2px;
}

/* Hero Section */
.hero {
    position: relative;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    overflow: hidden;
    padding-top: 120px; /* Compensate for fixed header */

}

.hero-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: -2;
    background-image: url('../images/gallery/heroback.jpg');
    background-size: cover;
    background-position: center;
    background-attachment: fixed;
    background-repeat: no-repeat;
}

.hero-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(45deg, rgba(0, 0, 0, 0.6), rgba(26, 26, 26, 0.4));
    z-index: -1;
}

.hero-content {
    text-align: center;
    color: var(--white);
    max-width: 800px;
    padding: 0 2rem;
}

.hero-title {
    font-size: clamp(3rem, 6vw, 5rem);
    font-weight: 700;
    margin-bottom: 1rem;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
}

.hero-subtitle {
    font-size: clamp(1.2rem, 2.5vw, 1.8rem);
    font-weight: 500;
    margin-bottom: 1.5rem;
    color: var(--white);
}

.hero-description {
    font-size: clamp(1rem, 1.5vw, 1.2rem);
    color: #f8f8f8;
    margin-bottom: 2rem;
    opacity: 0.9;
}

.hero-cta {
    display: flex;
    gap: 1rem;
    justify-content: center;
    flex-wrap: wrap;
}

/* Buttons */
.btn {
    display: inline-block;
    padding: 1rem 2rem;
    text-decoration: none;
    border-radius: 50px;
    font-weight: 600;
    transition: var(--transition);
    cursor: pointer;
    border: none;
    font-size: 1rem;
    text-align: center;
}

.btn-primary {
    background: var(--accent-color);
    color: var(--white);
}

.btn-primary:hover {
    background: var(--accent-light);
    transform: translateY(-3px);
    box-shadow: var(--shadow-hover);
}

.btn-secondary {
    background: transparent;
    color: var(--white);
    border: 2px solid var(--white);
}

.btn-secondary:hover {
    background: var(--white);
    color: var(--primary-color);
    transform: translateY(-3px);
}

.btn-large {
    padding: 1.2rem 2.5rem;
    font-size: 1.1rem;
}

/* Sections */
section {
    padding: 5rem 0;
}

.section-header {
    text-align: center;
    margin-bottom: 4rem;
}

.section-header h2 {
    margin-bottom: 1rem;
}

.section-header p {
    font-size: 1.1rem;
    max-width: 600px;
    margin: 0 auto;
}

/* About Section */
.about {
    background: var(--secondary-color);
}

.about-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: center;
}

.about-image img {
    width: 100%;
    height: 500px;
    object-fit: cover;
    object-position: center top;
    border-radius: 10px;
    box-shadow: var(--shadow);
}

.about-text h2 {
    color: var(--primary-color);
    margin-bottom: 1.5rem;
}

.about-lead {
    font-size: 1.2rem;
    font-weight: 500;
    color: var(--accent-color);
    margin-bottom: 1.5rem;
}

.about-stats {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 2rem;
    margin-top: 2rem;
}

.stat {
    text-align: center;
}

.stat-number {
    display: block;
    font-size: 2.5rem;
    font-weight: 700;
    color: var(--accent-color);
}

.stat-label {
