/* CTA Section */
.cta-section {
    background: linear-gradient(135deg, var(--accent-color), var(--accent-light));
    color: var(--white);
    text-align: center;
}

.cta-content h2 {
    color: var(--white);
    margin-bottom: 1rem;
}

.cta-content p {
    color: rgba(255, 255, 255, 0.9);
    font-size: 1.1rem;
    margin-bottom: 2rem;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
}

/* Footer */
.footer {
    background: var(--primary-color);
    color: var(--white);
    padding: 3rem 0 1rem;
}

.footer-content {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-bottom: 2rem;
}

.footer-section h3 {
    color: var(--white);
    margin-bottom: 1rem;
}

.footer-section h4 {
    color: var(--white);
    margin-bottom: 1rem;
    font-size: 1.1rem;
}

.footer-section p {
    color: rgba(255, 255, 255, 0.7);
}

.footer-section ul {
    list-style: none;
}

.footer-section ul li {
    margin-bottom: 0.5rem;
}

.footer-section ul li a {
    color: rgba(255, 255, 255, 0.7);
    text-decoration: none;
    transition: var(--transition);
}

.footer-section ul li a:hover {
    color: var(--white);
}

.footer-social {
    display: flex;
    gap: 1rem;
    margin-top: 1rem;
}

.footer-social a {
    font-size: 1.5rem;
    transition: var(--transition);
}

.footer-social a:hover {
    transform: translateY(-3px);
}

.footer-bottom {
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    padding-top: 1rem;
    text-align: center;
    color: rgba(255, 255, 255, 0.5);
}

/* Animations */
.fade-in {
    opacity: 1;
    transform: translateY(0);
    transition: all 0.6s ease;
}

.fade-in.visible {
    opacity: 1;
    transform: translateY(0);
}

/* Responsive Design */
@media (max-width: 768px) {
    .nav-menu {
        position: fixed;
        top: 80px;
        left: 0;
        right: 0;
        background: var(--white);
        flex-direction: column;
        padding: 2rem;
        box-shadow: var(--shadow);
        transform: translateY(-100%);
        opacity: 0;
        visibility: hidden;
        transition: all 0.3s ease;
        z-index: 999;
        max-height: calc(100vh - 80px);
        overflow-y: auto;
    }

    .nav-menu.active {
        transform: translateY(0);
        opacity: 1;
        visibility: visible;
    }

    .hamburger {
        display: flex;
    }

    .hamburger.active span:nth-child(1) {
        transform: rotate(45deg) translate(6px, 6px);
    }

    .hamburger.active span:nth-child(2) {
        opacity: 0;
        transform: translateX(-20px);
    }

    .hamburger.active span:nth-child(3) {
        transform: rotate(-45deg) translate(6px, -6px);
    }

    .nav-menu .nav-link {
        padding: 1rem 0;
        border-bottom: 1px solid rgba(0, 0, 0, 0.1);
        width: 100%;
        text-align: center;
        font-size: 1.1rem;
    }

    .nav-menu .nav-link:last-child {
        border-bottom: none;
    }

    .nav-menu .nav-link:hover {
        background: rgba(0, 0, 0, 0.05);
        border-radius: 8px;
    }

    /* Styles pour le menu déroulant sur mobile */
    .nav-dropdown {
        width: 100%;
    }

    .nav-dropdown-toggle {
        padding: 1rem 0;
        border-bottom: 1px solid rgba(0, 0, 0, 0.1);
        width: 100%;
        text-align: center;
        font-size: 1.1rem;
        justify-content: center;
    }

    .nav-dropdown-content {
        position: static;
        box-shadow: none;
        background: rgba(0, 0, 0, 0.02);
        border-radius: 0;
        margin-top: 0;
        padding: 0;
    }

    .nav-dropdown:hover .nav-dropdown-content,
    .nav-dropdown.active .nav-dropdown-content {
        display: block;
    }

    .nav-dropdown-content a {
        padding: 0.75rem 1.5rem;
        font-size: 1rem;
        border-bottom: 1px solid rgba(0, 0, 0, 0.05);
    }

    .nav-dropdown-content a:hover {
        background: rgba(0, 0, 0, 0.05);
        padding-left: 2rem;
    }

    .about-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .about-stats {
        grid-template-columns: 1fr;
        gap: 1rem;
    }

    .valeurs-grid {
        grid-template-columns: 1fr;
    }

    .formations-grid {
        grid-template-columns: 1fr;
    }

    .models-grid {
        grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
        gap: 1.5rem;
    }

    .model-measurements {
        grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
        gap: 0.75rem;
    }

    .model-characteristics {
        grid-template-columns: repeat(auto-fit, minmax(80px, 1fr));
        gap: 0.75rem;
    }

    .gallery-grid {
        grid-template-columns: repeat(auto-fill, minmax(150px, 1fr));
        gap: 0.75rem;
    }

    .gallery-container {
        padding: 1rem;
    }

    .hero-cta {
        flex-direction: column;
        align-items: center;
    }

    .btn {
        padding: 0.8rem 1.5rem;
    }

    .container {
        padding: 0 1rem;
    }

    section {
        padding: 3rem 0;
    }
}

@media (max-width: 480px) {
    .nav-logo h2 {
        font-size: 1rem;
    }

    .models-grid {
        grid-template-columns: 1fr;
    }

    .footer-content {
        grid-template-columns: 1fr;
        text-align: center;
    }
}

/* Smooth scrolling */
html {
    scroll-behavior: smooth;
}

/* Loading animation for images */
img {
    opacity: 1;
    transition: opacity 0.3s ease;
}

img:not([src]),
img[src=""] {
    opacity: 0;
}

/* Error message styling */
.error-message {
    text-align: center;
    padding: 2rem;
    color: var(--text-secondary);
    font-size: 1.1rem;
    background: var(--secondary-color);
    border-radius: 10px;
    margin: 2rem 0;
}

/* Model Profile Page Styles */
.profile-header {
    padding: 120px 20px 40px;
    text-align: center;
    position: relative;
}

.btn-back {
    display: inline-flex;
    align-items: center;
